pragma solidity 0.7.4;

struct AppStorage {
    mapping(address => mapping(address => bool)) approved;
    mapping(address => uint256) balances;
    mapping(uint256 => uint256) wearableVouchers;
    // enables us to add additional map slots here
    bytes32[1000] emptyMapSlots;
    address contractOwner;
    address ghstContract;
    string name;
}

contracts/test/MyFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.4;

import "./AppStorage.sol";
import "./LibERC20.sol";
import "./LibName.sol";

/// @dev Note: the ERC-165 identifier for this interface is 0x150b7a02.
contract MyFacet1 {
    using LibERC20 for AppStorage;
    using LibName for AppStorage;
    AppStorage s;

    function balanceOf(address _account) external view returns (uint256 balance_) {
        balance_ = s.balances[_account];
    }

    function burn(uint256 _value) external {
        s.transfer(address(0), _value);
    }

    function getName() external view returns (string memory name_) {
        name_ = s.getName();
    }
}

contracts/test/LibERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.4;

import "./AppStorage.sol";

library LibERC20 {
    event Transfer(address indexed _from, address indexed _to, uint256 _value);

    function transfer(
        AppStorage storage s,
        address _to,
        uint256 _value
    ) internal returns (bool success) {
        uint256 frombalances = s.balances[msg.sender];
        require(frombalances >= _value, "LibERC20: Not enough balance to transfer");
        s.balances[msg.sender] = frombalances - _value;
        s.balances[_to] += _value;
        emit Transfer(msg.sender, _to, _value);
        success = true;
    }
}

contracts/test/LibName.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.7.4;

import "./AppStorage.sol";

library LibName {
    function getName(AppStorage storage s) internal view returns (string memory name_) {
        name_ = s.name;
    }
}
